import React, { Component } from 'react';
import './App.css';
import './assets/cast.css';
import $ from 'jquery';
import {Temporadas} from './components/temporadas';
import {Episodio} from './components/episodio';
import {Elenco} from './components/elenco';


class App extends React.Component {
  constructor(props) {
    super(props);
    this.scroll = this.scroll.bind(this)
    this.state ={
      castBackground:'https://occ-0-894-1123.1.nflxso.net/art/0ef00/005f864851e4be98e96f55020ce769fba680ef00.jpg',
      title: '',
      selectTemporada:1,
      subTitle:'80% INDICADO / CIENCIA FICCIÃ“N / 2015 / EUA /14',
      sinopse:'',
      temporadas:[],
      width: null,
      cast:[]
    }


  }

  componentDidMount(){




    this.requestAPI()

  }



  async requestAPI(){
    fetch('https://sample-api-78c77.firebaseio.com/tv-shows/SHOW123.json')
    .then((resp) => resp.json()).then((res)=>{
        console.log(res)

    document.getElementById('title_id').innerText = res.Title;
    document.getElementById('subTitle_id').innerText = this.state.subTitle;

    document.getElementById('sinopse_id').innerText = res.Synopsis;

        this.setState({title:res.Title,cast:res,sinopse:res.Synopsis});
    })

    fetch('https://sample-api-78c77.firebaseio.com/episodes/SHOW123.json')
    .then((resp) => resp.json()).then((res)=>{
        console.log(res)
        this.setState({temporadas:res})
    })

    this.CreateLayout();
  }

  async CreateLayout(){

    document.getElementById('container-2_id').style = 
    `background-image: url(${this.state.castBackground});`;


  }

  scroll(direction){
    const width = $('.image-container').innerWidth();
    let far = width/20*direction;   
    let pos = $('.image-container').scrollLeft() + far;
    $('.image-container').animate( { scrollLeft: pos }, 500)
  }

 async clickMenu(ref){

  document.getElementById('id_general_barra').style.display = 'none';
  document.getElementById('id_elenco_barra').style.display = 'none';
  document.getElementById('id_premios_barra').style.display = 'none';

   document.getElementById(ref+'_barra').style.display = 'block';

   if(ref === 'id_general'){
    document.getElementById('elenco_menu').style.display = 'none';
    document.getElementById('general_menu').style.display = 'flex';
    document.getElementById('premios_menu').style.display = 'none';
   }

   if(ref === 'id_elenco'){
    document.getElementById('elenco_menu').style.display = 'flex';
    document.getElementById('general_menu').style.display = 'none';
    document.getElementById('premios_menu').style.display = 'none';
  }

  if(ref === 'id_premios'){
    document.getElementById('elenco_menu').style.display = 'none';
    document.getElementById('general_menu').style.display = 'none';
    document.getElementById('premios_menu').style.display = 'flex';
  }



  }


  render() {
    return (
      <div className="App">
        <div className='container'>
            <div className='container-2' id='container-2_id'>
                <div className='container-4'>
                      <div className='container-6'>
                          <p id='title_id' className='title'> </p>
                          <p id='subTitle_id' className='subTitle'> </p>
                      </div>

                      <div className='container-7'>
                        <div className='container-8'>
                            <div className='container-9'>
                                 <Temporadas temporadas={this.state.temporadas}/>
                            </div>

                            <div className='container-12'>
                                <div className='container-11r' />

                            </div>

                            <div className='container-10'>

                              <Episodio temporadas={this.state.temporadas}
                              selectTemporada={this.state.selectTemporada}
                              />                

                            </div>
                        </div>
                      </div>
                </div>


            </div>

            <div className='container-3'>
              <div className='container-14'>
                  <div className='container-16'>
                      <div className='container-24' id='id_general' onClick={()=>this.clickMenu('id_general')}>
                        <p className='text3'>GENERAL</p>
                       </div>

                       <div className='container-25'id='id_elenco' onClick={()=>this.clickMenu('id_elenco')}>
                        <p className='text3'>ELENCO</p>
                       </div>

                       <div className='container-26' id='id_premios' onClick={()=>this.clickMenu('id_premios')}>
                        <p className='text3'>PRINCIPALES PREMIOS</p>
                       </div>

                       <div className='container-20'>



                        <div className='tele'>
                            <svg xmlns="http://www.w3.org/2000/svg"  width="49" height="30" viewBox="0 0 49 30">
                                <defs>
                                    <path id="a" d="M0 .417h47.959v27.747H0z"/>
                                </defs>
                                <g fill="none" fillRule="evenodd">
                                    <g transform="translate(.376 1.834)">

                                        <path fill="#FFF" d="M19.85 15.375h-.138c-1.028 0-2.055.002-3.082-.004-.129 0-.15.04-.15.149.003 2.964.003 5.927 0 8.89 0 .117.034.146.154.145 1.02-.004 2.04-.002 3.059-.002.157 0 .157 0 .157-.147v-9.031zm-9.923 5.667c-.15.135-.278.266-.422.379-.47.366-1.003.554-1.612.434-1.329-.26-2.092-1.795-1.235-2.945a1.88 1.88 0 0 1 1.54-.787c.502-.01.942.175 1.333.465.135.1.26.212.394.324l2.65-1.709c-1.486-1.863-3.723-2.688-6.034-1.918-1.89.63-3.828 2.521-3.496 5.167.22 1.753 1.439 3.906 4.208 4.413a5.31 5.31 0 0 0 5.26-2.082l-2.586-1.741zM37.38 15.42v.137l-.001 1.605-.015 7.24c0 .088-.01.157.13.157 2.392-.005 4.783-.004 7.175-.002.107 0 .135-.03.135-.129-.005-.626-.002-1.252-.002-1.878v-.132h-4.094v-1.256H44.6c.173 0 .173 0 .173-.168v-1.97c0-.156 0-.156-.163-.156H40.71v-1.35h4.045V15.42H37.38zm-13.447-.339c-.005.064-.01.1-.01.136 0 3.059 0 6.118-.002 9.176 0 .103.033.127.137.127.923-.004 1.847-.002 2.77-.003.04 0 .077-.004.13-.007v-3.553l6.177 3.961V15.4H33c-.876 0-1.751.002-2.627-.003-.118 0-.145.034-.145.141.004 1.108.003 2.216.003 3.324v.144l-6.297-3.925zM0 16.607v-4.144l.12.003 11.29.014 6.755.006 13.005.013c2.228.003 4.455.007 6.683.009l9.97.007h.136v15.648h-.15l-2.795-.003-5.626-.006-8.447-.004c-6.451-.002-12.902-.002-19.353-.005-3.795-.001-7.59-.005-11.386-.008-.17 0-.17 0-.17-.158L.014 16.787c0-.06-.01-.12-.014-.18z" mask="url(#b)"/>
                                    </g>
                                    <path fill="#FFF" d="M23.241.436v2.25h-4.235v1.48h4.248v2.43h-4.253v1.369h4.28v2.267h-7.834V.436zM14.038.436v2.578h-2.78v7.218h-3.45v-7.21h-2.91V.436zM36.545 10.232V.436h7.8v2.243h-4.291v1.463h4.299v2.442h-4.296V7.97h4.323v2.262zM29.124 7.612h3.401v2.62h-6.529V.436h3.128z"/>
                                </g>
                            </svg>
                        </div>

                       </div>
                  </div>

                  <div className='container-17'>
                      <div className='container-24'>
                        <div className='container-21' id='id_general_barra'> </div>
                       </div>

                       <div className='container-25'>
                         <div className='container-22' id='id_elenco_barra' style={{display:'none'}} />
                       </div>

                       <div className='container-26'>
                       <div className='container-23' id='id_premios_barra' style={{display:'none'}} />
                       </div>

                       <div className='container-20'>

                       </div>
                  </div>

                  <div className='container-18' id='general_menu' >
                     <div className='container-27'>

                        <div className='container-29'>
                          <svg xmlns="http://www.w3.org/2000/svg" width="34" height="34" viewBox="0 0 34 34">
                              <g fill="none" fillRule="evenodd" stroke="#9B9B9B">
                                  <path fillRule="nonzero" strokeWidth="1.5" d="M17 33c8.822 0 16-7.177 16-16 0-8.822-7.178-16-16-16S1 8.177 1 17c0 8.822 7.178 16 16 16z"/>
                                  <g transform="translate(10 10)">
                                      <rect width="1" height="13" x="6.5" y=".5" rx=".5"/>
                                      <rect width="1" height="13" x="6.5" y=".5" rx=".5" transform="rotate(90 7 7)"/>
                                  </g>
                              </g>
                          </svg>

                          <p className='text6'> Mi Lista</p>
                        </div>

                        <div className='container-29'>
                          <svg xmlns="http://www.w3.org/2000/svg" width="34" height="34" viewBox="0 0 34 34">
                              <g fill="none" fillRule="evenodd" transform="translate(1 1)">
                                  <path stroke="#9B9B9B" strokeWidth="1.5" d="M16 32c8.822 0 16-7.177 16-16 0-8.822-7.178-16-16-16S0 7.177 0 16c0 8.822 7.178 16 16 16z"/>
