                                  <circle cx="7.111" cy="14.222" r="1.778" fill="#9B9B9B"/>
                                  <circle cx="24.889" cy="14.222" r="1.778" fill="#9B9B9B"/>
                              </g>
                          </svg>


                          <p className='text6'>Evaluar</p>
                        </div>


                        <div className='container-29'>
                          <svg xmlns="http://www.w3.org/2000/svg" width="34" height="34" viewBox="0 0 34 34">
                              <g fill="none" fillRule="evenodd" transform="translate(1 1)">
                                  <path stroke="#9B9B9B" strokeWidth="1.5" d="M16 32c8.822 0 16-7.177 16-16 0-8.822-7.178-16-16-16S0 7.177 0 16c0 8.822 7.178 16 16 16z"/>
                                  <circle cx="16.143" cy="16.143" r="3.699" fill="#9B9B9B"/>
                              </g>
                          </svg>

                          <p className='text6'>Grabar</p>
                        </div>

                        <div className='container-29'>
                          <svg xmlns="http://www.w3.org/2000/svg" width="34" height="34" viewBox="0 0 34 34">
                              <g fill="none" fillRule="nonzero">
                                  <path stroke="#9B9B9B" strokeWidth="1.5" d="M17 33c8.822 0 16-7.177 16-16 0-8.822-7.178-16-16-16S1 8.177 1 17c0 8.822 7.178 16 16 16z"/>
