                                  <path fill="#9B9B9B" d="M22.959 13H20.66a.863.863 0 0 0-.862.864c0 .478.386.865.862.865h1.436v8.644H12.33V14.73h1.436a.863.863 0 0 0 .862-.865.863.863 0 0 0-.862-.864H11.47a.863.863 0 0 0-.862.864v10.374c0 .477.386.864.862.864h11.489a.863.863 0 0 0 .861-.864V13.864A.863.863 0 0 0 22.96 13z"/>
                              </g>
                          </svg>


                          <p className='text6'>Compartir</p>
                        </div>



                     </div>

                     <div className='container-28'>
                     <p className='text4'>SINOPSE</p>
                      <p className='text5' id='sinopse_id'></p>
                     </div>
                  </div>

                  <div className='container-18' id='elenco_menu' style={{display:'none'}}>
                      <div className="wrapper">
                            <a className="prev" onClick={this.scroll.bind(null,-1)}>&#10094;</a>

                            <Elenco cast={this.state.cast}/>

                            <a className="next" onClick={this.scroll.bind(null,1)}>&#10095;</a>
                        </div>
                  </div>


                  <div className='container-18' id='premios_menu' style={{display:'none'}}>

                  </div>
              </div>
            </div>
        </div>
      </div>
    );
  }
}

export default App;
