import React, { Component } from 'react';



export const BarraTemporadas=({temporadas})=>{

    const getTemporadas = [];

      for(var i=0;i<temporadas.length;i++){
        getTemporadas.push(i);
     }



    const listItems = getTemporadas.map((number) =>
                <div className='container-11r' id={'barraTemporadas'+number} style={{display:'none'}} />
     );

    return(
        <div style={{display:'flex'}}> 
         {listItems}
        </div>
     )

}

export const Temporadas=({temporadas})=>{

    const getTemporadas = [];

      for(var i=0;i<temporadas.length;i++){
        getTemporadas.push(i);
     }

     const clickContainer =(id)=>{

        for(var i=0;i<getTemporadas.length;i++){
            console.log('barraTemporadas'+i)
            if(i != id){
            document.getElementById('barraTemporadas'+i).style.display = 'none';
            }else{
                document.getElementById('barraTemporadas'+i).style.display = 'none';
            }
        }
     }


    const listItems = getTemporadas.map((number) =>
        <div className='container-11' key={number} onClick={()=>{clickContainer(number)}}>
            <p className='text1'>T{number}</p>
        </div>
     );

    return(
       <div style={{display:'flex'}}> 
        {listItems}
       </div>
    )
}
