import React, { Component } from 'react';


export const Temporadas=({temporadas})=>{

    const getTemporadas = [];

      for(var i=0;i<temporadas.length;i++){
        getTemporadas.push(i);
     }


    const listItems = getTemporadas.map((number) =>
        <div className='container-11' key={number}>
            <p className='text1'>T{number}</p>
        </div>
     );

    return(
       <div style={{display:'flex'}}> 
        {listItems}
       </div>
    )
}




export const Episodio=({temporadas,selectTemporada})=>{

    const getEpisodios = [];

      for(var i=0;i<temporadas.length;i++){


        if(temporadas[i]!=null){

            let idTemporadas = temporadas[i].ID.replace(/[^0-9.]+/, '');
            getEpisodios.push({
                id:i,
                Title:temporadas[i].Title,
                ref:idTemporadas
            });
        }
     }

    const clickContainer =()=>{
        alert('asd')
    }




    const listItems = getEpisodios.map((number) =>
            <div className='container-13' key={number.id} id={'id_card_'+number.id}>

            <div className='container-30' id={'id_container_'+number.id} onClick={()=>{clickContainer()}}>
                <p className='text2'>{number.ref} {number.Title}</p>

                    <div className='play'>
                    <svg xmlns="http://www.w3.org/2000/svg" width="30" height="30" viewBox="0 0 40 40">
                        <g fill="none" fillRule="evenodd">
                            <circle cx="20" cy="20" r="19" stroke="#D8D8D8" strokeWidth="2"/>
                            <path fill="#D8D8D8" d="M14.881 13.957v12.924a1 1 0 0 0 1.503.864l10.983-6.388a1 1 0 0 0 .008-1.724l-10.983-6.535a1 1 0 0 0-1.51.86z"/>
                        </g>
                    </svg>
                    </div>
            </div>

            <div className='episode' id={'id_episodio_'+number} style={{display:'none'}}>

                    <div className='imgEpisode'>
                        <img className='imgEpisode' src='https://occ-0-894-1123.1.nflxso.net/art/e0e90/292975320f88a9f3fc741c132d0ec2ac20ce0e90.webp'/>
                        <div className='divCap'>
                        <div style={{display:'flex',width:'100%'}}>
                            <div className='divCapR'/>
                        </div>
                        </div>
                    </div>

                    <div className='episodeDescription'>
                        <div className='divEpisode'>
                        <p className='text7'> aasasdasdasd asdasdasdasd asdlknasjkd asjkd asjdk asjd asjkd asjkd asjkd asjd asjkd asjkd jaksdjasdjkadkas djkasadkasdasdkasndkjasndkjasdnjkas djkas djkasd jkasdjkas jkasd jaks dajksd kasjasdasdasdasdasdasdas das das d asdasdasdasd asdasdasdsadasdsaasdasndkjasdjhas dhjasd asjd askjd askjd asjd ajsk dasjk dsamd asjdjsajdasd asd asd sad sadasdsad sasdasdasdasdasd sdasdasd </p>
                        </div>
                    </div>

            </div>

        </div>
     );

    return(
       <div style={{display:'flex',flexDirection:'column'}}> 
        {listItems}
       </div>
    )
}


